CREATE TABLE `sikayetler` (  
`id` int(10) unsigned NOT NULL AUTO_INCREMENT,  
`ilan_id` int(10) unsigned NOT NULL,  
`mesaj` varchar(255) NOT NULL,  
`tarih` datetime NOT NULL,  
PRIMARY KEY (`id`),  
KEY `fk_sikayetler_ilan_idx` (`ilan_id`),  
CONSTRAINT `fk_sikayetler_ilan` FOREIGN KEY (`ilan_id`) REFERENCES `ilanlar` (`id`) 
ON DELETE NO ACTION ON UPDATE NO ACTION) 
ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

CREATE ALGORITHM=UNDEFINED DEFINER=`publicUsr`@`%` SQL SECURITY DEFINER VIEW `dortlerOto`.`sikayetler_datatable` AS 
SELECT 
  `t1`.`id` AS `id`,
  `t1`.`mesaj` AS `mesaj`,
  `t1`.`tarih` AS `tarih`,
  `t1`.`ilan_id` AS `ilan_id`,
  `t2`.`baslik` AS `baslik`
FROM `dortlerOto`.`sikayetler` `t1`
JOIN `dortlerOto`.`ilanlar` AS `t2` ON `t2`.`id` = `t1`.`ilan_id`;DROP TABLE IF EXISTS `facilities`;CREATE TABLE `facilities` (  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,  `code` varchar(255) DEFAULT NULL,  `value` varchar(255) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;/*Data for the table `facilities` */insert  into `facilities`(`id`,`code`,`value`) values (1,'GUVENLIK','Güvenlik'),(2,'DONANIM','Iç Donanim'),(3,'DIS_DONANIM','Dis Donanim'),(4,'MULTIMEDYA','Multimedya'),(5,'BOYALI','Boyali Parça'),(6,'DEGISEN','Degisen Parça');/*Table structure for table `facility_features` */DROP TABLE IF EXISTS `facility_features`;CREATE TABLE `facility_features` (  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,  `code` varchar(255) DEFAULT NULL,  `feature` varchar(255) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=121 DEFAULT CHARSET=utf8;/*Data for the table `facility_features` */insert  into `facility_features`(`id`,`code`,`feature`) values (1,'GUVENLIK','ABC'),(2,'GUVENLIK','ABS'),(3,'GUVENLIK','ASR'),(4,'GUVENLIK','ESP'),(5,'GUVENLIK','Airmatic'),(6,'GUVENLIK','EDL'),(7,'GUVENLIK','EBD'),(8,'GUVENLIK','TCS'),(9,'GUVENLIK','BAS'),(10,'GUVENLIK','Distronic'),(11,'GUVENLIK','Yokus Kalkis Destegi'),(12,'GUVENLIK','Gece Görüs'),(13,'GUVENLIK','Seritten Ayrilma Ikazi'),(14,'GUVENLIK','Serit Degistirme Yardimcisi'),(15,'GUVENLIK','Hava Yastigi (Sürücü)'),(16,'GUVENLIK','Hava Yastigi (Yolcu)'),(17,'GUVENLIK','Hava Yastigi (Yan)'),(18,'GUVENLIK','Hava Yastigi (Diz)'),(19,'GUVENLIK','Hava Yastigi (Perde)'),(20,'GUVENLIK','Hava Yastigi (Tavan)'),(21,'GUVENLIK','Lastik Ariza Göstergesi'),(22,'GUVENLIK','Yorgunluk Tespit Sistemi'),(23,'GUVENLIK','Isofix'),(24,'GUVENLIK','Alarm'),(25,'GUVENLIK','Merkezi Kilit'),(26,'GUVENLIK','Immobilizer'),(27,'DONANIM','Deri Koltuk'),(28,'DONANIM','Kumas Koltuk'),(29,'DONANIM','Deri / Kumas Koltuk'),(30,'DONANIM','Elektrikli Ön Camlar'),(31,'DONANIM','Elektri